        ctx.fillRect(10, 10, 200, 60);
        ctx.fillStyle = 'white';
        ctx.font = '14px monospace';
        ctx.fillText(`FPS: ${this.fps}`, 20, 30);
        ctx.fillText(`Memory: ${this.memoryUsage}MB`, 20, 50);
    }
}

