                // ì²˜ë¦¬ ì‹œê°„ ì¸¡ì •
                const endTime = performance.now();
                this.optimizer.frameTime = endTime - startTime;

                // ì ì‘í˜• ìƒ˜í”Œë§ ì¡°ì •
                if (this.optimizer.adaptiveEnabled) {
