                const performanceScore = this.calculatePerformanceScore();
                let newSampleRate = this.optimizer.currentSampleRate;

                if (performanceScore > 0.8) {
                    // ì„±ëŠ¥ì´ ì¢‹ìœ¼ë©´ ìƒ˜í”Œë§ ì†ë„ ì¦ê°€
                    newSampleRate = Math.min(
