                this.goal = 1000;
                this.lastAcceleration = { x: 0, y: 0, z: 0 };
                this.threshold = 12; // ê±¸ìŒ ê°ì§€ ì„ê³„ê°’
                this.lastStepTime = 0;
                this.minStepInterval = 300; // ìµœì†Œ ê±¸ìŒ ê°„ê²© (ms)

                this.initializeSDK();
