                    timer.textContent = remaining;

                    effectElement.appendChild(timer);
                    this.statusEffects.appendChild(effectElement);
                });
            }

            startRegeneration() {
                setInterval(() => {
                    // ì²´ë ¥ ìë™ íšŒë³µ (ì²œì²œíˆ)
