class MemoryManager {
    constructor(maxMemoryMB = 100) {
        this.maxMemory = maxMemoryMB * 1024 * 1024; // ë°”ì´íŠ¸ ë‹¨ìœ„
        this.cache = new Map();
        this.memoryUsage = 0;
        this.setupMemoryMonitoring();
    }

    setupMemoryMonitoring() {
        setInterval(() => {
            this.checkMemoryUsage();
        }, 30000); // 30ì´ˆë§ˆë‹¤ ì²´í¬

        // ë©”ëª¨ë¦¬ ê²½ê³  ê°ì§€
        if ('memory' in performance) {
            setInterval(() => {
                const memory = performance.memory;
                if (memory.usedJSHeapSize > memory.jsHeapSizeLimit * 0.9) {
                    console.warn('ë©”ëª¨ë¦¬ ì‚¬ìš©ëŸ‰ì´ ë†’ìŠµë‹ˆë‹¤. ì •ë¦¬ë¥¼ ì‹œì‘í•©ë‹ˆë‹¤.');
                    this.forceCleanup();
                }
            }, 10000);
        }
    }

    set(key, value, ttl = 300000) { // ê¸°ë³¸ 5ë¶„ TTL
        // ë©”ëª¨ë¦¬ ì‚¬ìš©ëŸ‰ ì¶”ì •
        const estimatedSize = this.estimateSize(value);

        // ë©”ëª¨ë¦¬ ë¶€ì¡±ì‹œ ì •ë¦¬
        if (this.memoryUsage + estimatedSize > this.maxMemory) {
            this.cleanup();
        }

        // ìºì‹œ ì €ì¥
        this.cache.set(key, {
            value,
            timestamp: Date.now(),
            ttl,
            size: estimatedSize
        });

        this.memoryUsage += estimatedSize;
    }

    get(key) {
        const item = this.cache.get(key);
        if (!item) return null;

        // TTL ì²´í¬
        if (Date.now() - item.timestamp > item.ttl) {
            this.delete(key);
            return null;
        }

        return item.value;
    }

    delete(key) {
        const item = this.cache.get(key);
        if (item) {
            this.memoryUsage -= item.size;
            this.cache.delete(key);
        }
    }

    cleanup() {
        const now = Date.now();
        const itemsToDelete = [];

        // ë§Œë£Œëœ í•­ëª© ì°¾ê¸°
        for (const [key, item] of this.cache.entries()) {
            if (now - item.timestamp > item.ttl) {
                itemsToDelete.push(key);
            }
        }

        // ë§Œë£Œëœ í•­ëª© ì‚­ì œ
        itemsToDelete.forEach(key => this.delete(key));

        // ì—¬ì „íˆ ë©”ëª¨ë¦¬ê°€ ë¶€ì¡±í•˜ë©´ LRU ë°©ì‹ìœ¼ë¡œ ì‚­ì œ
        if (this.memoryUsage > this.maxMemory * 0.8) {
            const sortedItems = Array.from(this.cache.entries())
                .sort((a, b) => a[1].timestamp - b[1].timestamp);

            const toDelete = sortedItems.slice(0, Math.floor(sortedItems.length * 0.3));
            toDelete.forEach(([key]) => this.delete(key));
        }
    }

    forceCleanup() {
        this.cache.clear();
        this.memoryUsage = 0;

        // ê°€ë¹„ì§€ ì»¬ë ‰ì…˜ íŒíŠ¸
        if (window.gc) {
            window.gc();
        }
    }

    estimateSize(obj) {
        // ëŒ€ëµì ì¸ ê°ì²´ í¬ê¸° ê³„ì‚°
