class SensorDataValidator {
    constructor() {
        this.validationRules = this.createValidationRules();
        this.outlierDetector = new OutlierDetector();
        this.dataHistory = [];
        this.maxHistorySize = 100;
    }

    createValidationRules() {
        return {
            orientation: {
                alpha: { min: 0, max: 360, type: 'number' },
                beta: { min: -180, max: 180, type: 'number' },
                gamma: { min: -90, max: 90, type: 'number' }
            },
            acceleration: {
                x: { min: -50, max: 50, type: 'number' },
                y: { min: -50, max: 50, type: 'number' },
                z: { min: -50, max: 50, type: 'number' }
            },
            rotationRate: {
                alpha: { min: -360, max: 360, type: 'number' },
                beta: { min: -360, max: 360, type: 'number' },
                gamma: { min: -360, max: 360, type: 'number' }
            }
        };
    }

    validate(sensorData) {
        const errors = [];
        const warnings = [];

        // ê¸°ë³¸ êµ¬ì¡° ê²€ì¦
        if (!sensorData || !sensorData.data) {
            errors.push('ì„¼ì„œ ë°ì´í„° êµ¬ì¡°ê°€ ì˜¬ë°”ë¥´ì§€ ì•ŠìŠµë‹ˆë‹¤');
            return { valid: false, errors, warnings };
        }

        // íƒ€ì„ìŠ¤íƒ¬í”„ ê²€ì¦
        if (!this.isValidTimestamp(sensorData.timestamp)) {
            errors.push('ìœ íš¨í•˜ì§€ ì•Šì€ íƒ€ì„ìŠ¤íƒ¬í”„ì…ë‹ˆë‹¤');
        }

        // ê° ì„¼ì„œ íƒ€ì…ë³„ ê²€ì¦
        for (const [sensorType, sensorValues] of Object.entries(sensorData.data)) {
            if (this.validationRules[sensorType]) {
                const sensorErrors = this.validateSensorType(sensorType, sensorValues);
                errors.push(...sensorErrors);
            }
        }

        // ì´ìƒê°’ ê°ì§€
        const outlierResult = this.detectOutliers(sensorData);
        warnings.push(...outlierResult.warnings);

        // ë°ì´í„° ì¼ê´€ì„± ê²€ì¦
        const consistencyResult = this.checkConsistency(sensorData);
        warnings.push(...consistencyResult.warnings);

        // íˆìŠ¤í† ë¦¬ì— ì¶”ê°€
        this.addToHistory(sensorData);

        return {
            valid: errors.length === 0,
            errors,
            warnings,
            quality: this.calculateDataQuality(sensorData, errors.length, warnings.length)
        };
    }

    validateSensorType(sensorType, sensorValues) {
        const errors = [];
        const rules = this.validationRules[sensorType];

        for (const [axis, value] of Object.entries(sensorValues)) {
            const rule = rules[axis];
            if (!rule) continue;

            // íƒ€ì… ê²€ì¦
            if (typeof value !== rule.type) {
                errors.push(`${sensorType}.${axis}: íƒ€ì…ì´ ${rule.type}ì´ì–´ì•¼ í•©ë‹ˆë‹¤ (ì‹¤ì œ: ${typeof value})`);
                continue;
            }

            // ë²”ìœ„ ê²€ì¦
            if (value < rule.min || value > rule.max) {
                errors.push(`${sensorType}.${axis}: ê°’ì´ ë²”ìœ„ë¥¼ ë²—ì–´ë‚¬ìŠµë‹ˆë‹¤ (${rule.min}~${rule.max}, ì‹¤ì œ: ${value})`);
            }

            // ë¬´í•œëŒ€ ë° NaN ê²€ì¦
            if (!isFinite(value)) {
                errors.push(`${sensorType}.${axis}: ìœ íš¨í•˜ì§€ ì•Šì€ ìˆ«ìê°’ì…ë‹ˆë‹¤ (${value})`);
            }
        }

        return errors;
    }

    detectOutliers(sensorData) {
        const warnings = [];

        // Z-Score ê¸°ë°˜ ì´ìƒê°’ ê°ì§€
        for (const [sensorType, sensorValues] of Object.entries(sensorData.data)) {
            for (const [axis, value] of Object.entries(sensorValues)) {
                const zScore = this.outlierDetector.calculateZScore(sensorType, axis, value);

                if (Math.abs(zScore) > 3) { // 3 í‘œì¤€í¸ì°¨ ì´ìƒ
                    warnings.push(`${sensorType}.${axis}: ì´ìƒê°’ ê°ì§€ (Z-Score: ${zScore.toFixed(2)})`);
                }
            }
        }

        return { warnings };
    }

    checkConsistency(sensorData) {
        const warnings = [];

        // ê°€ì†ë„ì™€ ì¤‘ë ¥ì˜ ì¼ê´€ì„± ê²€ì¦
        if (sensorData.data.acceleration && sensorData.data.accelerationIncludingGravity) {
            const acc = sensorData.data.acceleration;
            const accWithGravity = sensorData.data.accelerationIncludingGravity;

            // ì¤‘ë ¥ ì„±ë¶„ ê³„ì‚° (ì¼ë°˜ì ìœ¼ë¡œ Yì¶•ì— -9.8 m/sÂ²)
            const gravityComponent = {
                x: accWithGravity.x - acc.x,
                y: accWithGravity.y - acc.y,
                z: accWithGravity.z - acc.z
            };

            const totalGravity = Math.sqrt(
                gravityComponent.x ** 2 +
                gravityComponent.y ** 2 +
                gravityComponent.z ** 2
            );

            // ì§€êµ¬ ì¤‘ë ¥ê³¼ì˜ ì°¨ì´ ê²€ì¦ (9.8 m/sÂ²)
            if (Math.abs(totalGravity - 9.8) > 2.0) {
                warnings.push(`ì¤‘ë ¥ ë²¡í„°ê°€ ì˜ˆìƒ ë²”ìœ„ë¥¼ ë²—ì–´ë‚¨: ${totalGravity.toFixed(2)} m/sÂ²`);
            }
        }

        // ë°©í–¥ê³¼ ê°€ì†ë„ì˜ ì¼ê´€ì„± ê²€ì¦
        if (sensorData.data.orientation && sensorData.data.acceleration) {
            const orientation = sensorData.data.orientation;
            const acceleration = sensorData.data.acceleration;

            // ê¸°ìš¸ê¸°ì— ë”°ë¥¸ ì¤‘ë ¥ ë°©í–¥ ì˜ˆì¸¡
            const predictedGravityDirection = this.predictGravityFromOrientation(orientation);
            const actualGravityDirection = this.normalizeVector(acceleration);

            const correlation = this.calculateVectorCorrelation(predictedGravityDirection, actualGravityDirection);

            if (correlation < 0.7) { // 70% ë¯¸ë§Œ ìƒê´€ê´€ê³„
                warnings.push(`ë°©í–¥ê³¼ ê°€ì†ë„ ë°ì´í„° ê°„ ì¼ê´€ì„± ë¶€ì¡± (ìƒê´€ê³„ìˆ˜: ${correlation.toFixed(2)})`);
            }
        }

        return { warnings };
    }

    calculateDataQuality(sensorData, errorCount, warningCount) {
        let quality = 100;

        // ì˜¤ë¥˜ì— ë”°ë¥¸ í’ˆì§ˆ ì €í•˜
        quality -= errorCount * 20;
        quality -= warningCount * 5;

        // ë°ì´í„° ì™„ì„±ë„ í‰ê°€
        const completeness = this.calculateCompleteness(sensorData);
        quality *= completeness;

        // ë…¸ì´ì¦ˆ ë ˆë²¨ í‰ê°€
        const noiseLevel = this.calculateNoiseLevel(sensorData);
        quality *= (1 - noiseLevel);

        return Math.max(0, Math.min(100, quality));
    }

    calculateCompleteness(sensorData) {
        const expectedFields = ['orientation', 'acceleration', 'rotationRate'];
        const presentFields = expectedFields.filter(field =>
            sensorData.data[field] && Object.keys(sensorData.data[field]).length > 0
        );

        return presentFields.length / expectedFields.length;
    }

    calculateNoiseLevel(sensorData) {
        if (this.dataHistory.length < 10) return 0; // ì¶©ë¶„í•œ ë°ì´í„°ê°€ ì—†ìŒ

        let totalVariation = 0;
        let sampleCount = 0;

        // ìµœê·¼ ë°ì´í„°ì˜ ë³€ë™ì„± ê³„ì‚°
        const recentData = this.dataHistory.slice(-10);

        for (const sensorType of ['orientation', 'acceleration', 'rotationRate']) {
            if (!sensorData.data[sensorType]) continue;

            for (const axis of ['x', 'y', 'z', 'alpha', 'beta', 'gamma']) {
                const values = recentData
                    .map(d => d.data[sensorType] && d.data[sensorType][axis])
                    .filter(v => v !== undefined);

                if (values.length > 5) {
                    const variation = this.calculateVariation(values);
                    totalVariation += variation;
                    sampleCount++;
                }
            }
        }

        return sampleCount > 0 ? totalVariation / sampleCount : 0;
    }

    isValidTimestamp(timestamp) {
        const now = Date.now();
        const fiveMinutesAgo = now - 5 * 60 * 1000;
        const fiveMinutesFromNow = now + 5 * 60 * 1000;

        return timestamp >= fiveMinutesAgo && timestamp <= fiveMinutesFromNow;
    }

    addToHistory(sensorData) {
        this.dataHistory.push({
            ...sensorData,
            validationTime: Date.now()
        });

        if (this.dataHistory.length > this.maxHistorySize) {
            this.dataHistory.shift();
        }
    }

    predictGravityFromOrientation(orientation) {
        // ë°©í–¥ ì„¼ì„œ ê°’ì—ì„œ ì¤‘ë ¥ ë²¡í„° ì˜ˆì¸¡
        const { beta, gamma } = orientation;

        // ë¼ë””ì•ˆ ë³€í™˜
        const betaRad = (beta * Math.PI) / 180;
        const gammaRad = (gamma * Math.PI) / 180;

        // ì¤‘ë ¥ ë²¡í„° ê³„ì‚° (Zì¶•ì„ ìœ„ë¡œ, Yì¶•ì„ í™”ë©´ ìœ„ìª½ìœ¼ë¡œ ê°€ì •)
        return {
            x: Math.sin(gammaRad) * 9.8,
            y: Math.sin(betaRad) * 9.8,
            z: Math.cos(betaRad) * Math.cos(gammaRad) * 9.8
        };
    }

    normalizeVector(vector) {
        const magnitude = Math.sqrt(vector.x ** 2 + vector.y ** 2 + vector.z ** 2);
        return magnitude > 0 ? {
            x: vector.x / magnitude,
            y: vector.y / magnitude,
            z: vector.z / magnitude
        } : { x: 0, y: 0, z: 0 };
    }

    calculateVectorCorrelation(v1, v2) {
        return v1.x * v2.x + v1.y * v2.y + v1.z * v2.z;
    }

    calculateVariation(values) {
        const mean = values.reduce((sum, val) => sum + val, 0) / values.length;
        const variance = values.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) / values.length;
        return Math.sqrt(variance);
    }
