    applyComplementaryFilter(sensorData) {
        const { orientation, acceleration, gyroscope } = sensorData;

        // ê°€ì†ë„ê³„ì—ì„œ ê¸°ìš¸ê¸° ì¶”ì • (ì €ì£¼íŒŒ íŠ¹ì„± ì´ìš©)
        const accelTilt = this.estimateTiltFromAccel(acceleration);

        // ìì´ë¡œìŠ¤ì½”í”„ì—ì„œ íšŒì „ ì ë¶„ (ê³ ì£¼íŒŒ íŠ¹ì„± ì´ìš©)
        const gyroOrientation = this.integrateGyroscope(gyroscope);

        // ìƒí˜¸ ë³´ì™„ í•„í„° ì ìš© (ì¼ë°˜ì ìœ¼ë¡œ 98% ìì´ë¡œ, 2% ê°€ì†ë„ê³„)
        const alpha = 0.98;

        const fusedOrientation = {
            pitch: alpha * gyroOrientation.pitch + (1 - alpha) * accelTilt.pitch,
            roll: alpha * gyroOrientation.roll + (1 - alpha) * accelTilt.roll,
            yaw: orientation.alpha // ìê¸°ê³„ ì—†ì´ëŠ” ìì´ë¡œìŠ¤ì½”í”„ë§Œ ì‚¬ìš©
        };

        return {
            orientation: fusedOrientation,
            confidence: this.calculateComplementaryConfidence(accelTilt, gyroOrientation),
            components: {
                accelerometer: accelTilt,
                gyroscope: gyroOrientation,
                magnetometer: { yaw: orientation.alpha }
