    }

    estimateTiltFromAccel(acceleration) {
        const { x, y, z } = acceleration;

        // ì¤‘ë ¥ ë²¡í„°ì—ì„œ ê¸°ìš¸ê¸° ê³„ì‚°
        const pitch = Math.atan2(-x, Math.sqrt(y*y + z*z)) * 180 / Math.PI;
        const roll = Math.atan2(y, z) * 180 / Math.PI;

        return {
            pitch: pitch,
            roll: roll,
