    }

    calculateSensorReliability(sensors) {
        const reliabilities = {};

        // ë°©í–¥ ì„¼ì„œ ì‹ ë¢°ë„
        reliabilities.orientation = this.calculateOrientationReliability(sensors.orientation);

        // ê°€ì†ë„ê³„ ì‹ ë¢°ë„ (ì›€ì§ì„ì´ ì ì„ ë•Œ ë” ì‹ ë¢°ì„± ë†’ìŒ)
        reliabilities.acceleration = this.calculateAccelReliability(sensors.acceleration);

        // ìì´ë¡œìŠ¤ì½”í”„ ì‹ ë¢°ë„ (ë“œë¦¬í”„íŠ¸ ê³ ë ¤)
        reliabilities.rotation = this.calculateGyroReliability(sensors.rotation);

