    calculateAccelReliability(acceleration) {
        const magnitude = Math.sqrt(acceleration.x**2 + acceleration.y**2 + acceleration.z**2);

        // ì¤‘ë ¥ì— ê°€ê¹Œìš¸ìˆ˜ë¡ ë” ì‹ ë¢°ì„± ë†’ìŒ (ì •ì  ìƒíƒœ)
        const gravityDifference = Math.abs(magnitude - 9.8);
