            processSticking: (stickyBody, otherBody, collision) => {
                if (stickyBody.gameObject?.sticky) {
                    const stickForce = stickyBody.gameObject.stickiness;

                    // ì¶©ëŒ ì†ë„ê°€ ì„ê³„ê°’ ì´í•˜ë©´ ë¶™ì„
