    stickObjects(stickyBody, otherBody, stickiness) {
        // ìŠ¤í‹°í‚¤ ì»¨ìŠ¤íŠ¸ë ˆì¸íŠ¸ ìƒì„±
        const constraint = Matter.Constraint.create({
            bodyA: stickyBody,
