                    const constraint = this.physics.createAdvancedConstraints()
                        .createDistance(
                            segments[i].physicsBody,
                            segments[i + 1].physicsBody,
                            segmentLength
                        );

                    constraints.push(constraint);
                }

                return {
                    segments: segments,
                    constraints: constraints,
                    length: totalLength,

                    // ë¡œí”„ ì¥ë ¥ ê³„ì‚°
                    calculateTension: () => {
                        return constraints.map(constraint => {
