class MobileSensorUIOptimizer {
    constructor() {
        this.deviceInfo = this.detectDeviceCapabilities();
        this.orientationManager = new OrientationManager();
        this.touchOptimizer = new TouchOptimizer();
        this.batteryOptimizer = new BatteryOptimizer();
    }

    // ë””ë°”ì´ìŠ¤ ëŠ¥ë ¥ ê°ì§€
    detectDeviceCapabilities() {
        return {
            screenSize: {
                width: window.screen.width,
                height: window.screen.height,
                diagonal: Math.sqrt(window.screen.width**2 + window.screen.height**2),
                density: window.devicePixelRatio || 1
            },
            sensors: {
                orientation: 'DeviceOrientationEvent' in window,
                motion: 'DeviceMotionEvent' in window,
                proximity: 'DeviceProximityEvent' in window,
                light: 'DeviceLightEvent' in window
            },
            capabilities: {
                touch: 'ontouchstart' in window,
                vibration: 'vibrate' in navigator,
                battery: 'getBattery' in navigator,
                online: navigator.onLine
            },
            performance: {
                memory: navigator.deviceMemory || 'unknown',
                cores: navigator.hardwareConcurrency || 'unknown',
                connection: navigator.connection?.effectiveType || 'unknown'
            }
        };
    }

    // í™”ë©´ í¬ê¸°ë³„ UI ìµœì í™”
    createResponsiveUI() {
        const screenCategory = this.categorizeScreen();

        const responsiveUI = {
            layouts: {
                'small': this.createSmallScreenLayout(),
                'medium': this.createMediumScreenLayout(),
                'large': this.createLargeScreenLayout()
            },

            currentLayout: screenCategory,

            applyLayout: (category = this.currentLayout) => {
                const layout = this.layouts[category];
                if (layout) {
                    layout.apply();
                    this.adjustSensorAreas(category);
                    this.optimizeInteractionTargets(category);
                }
            },

            handleOrientationChange: () => {
                const newCategory = this.categorizeScreen();
                if (newCategory !== this.currentLayout) {
                    this.currentLayout = newCategory;
                    this.applyLayout(newCategory);
                }

                this.adjustForOrientation();
            }
        };

        // í™”ë©´ íšŒì „ ê°ì§€
        window.addEventListener('orientationchange', () => {
            setTimeout(() => {
                responsiveUI.handleOrientationChange();
            }, 100);
        });

        return responsiveUI;
    }

    categorizeScreen() {
        const { width, height, diagonal } = this.deviceInfo.screenSize;
        const minDimension = Math.min(width, height);

        if (diagonal < 5) return 'small';      // < 5ì¸ì¹˜
        if (diagonal < 7) return 'medium';     // 5-7ì¸ì¹˜
        return 'large';                        // > 7ì¸ì¹˜
    }

    createSmallScreenLayout() {
        return {
            apply: () => {
                document.body.classList.add('layout-small');

