// ì„±ëŠ¥ ì¸¡ì • í´ë˜ìŠ¤
class PerformanceMonitor {
    constructor() {
        this.metrics = {
            fps: 0,
            frameTime: 0,
            sensorLatency: 0,
            renderTime: 0,
            memoryUsage: 0
        };
        this.frameCount = 0;
        this.lastTime = performance.now();
        this.measurements = [];
    }

    startFrame() {
        this.frameStartTime = performance.now();
    }

    endFrame() {
        const currentTime = performance.now();
        const frameTime = currentTime - this.frameStartTime;
        this.frameCount++;

        // FPS ê³„ì‚°
        if (currentTime - this.lastTime >= 1000) {
            this.metrics.fps = this.frameCount;
            this.metrics.frameTime = frameTime;
            this.frameCount = 0;
            this.lastTime = currentTime;

            // ë©”ëª¨ë¦¬ ì‚¬ìš©ëŸ‰ (Chromeì—ì„œë§Œ ì‘ë™)
            if (performance.memory) {
                this.metrics.memoryUsage = Math.round(
                    performance.memory.usedJSHeapSize / 1048576
                );
            }

            this.logMetrics();
        }
    }

    measureSensorLatency(receivedTime) {
        const currentTime = performance.now();
        this.metrics.sensorLatency = currentTime - receivedTime;
    }

    logMetrics() {
        console.log('ğŸ“Š Performance Metrics:', {
            fps: this.metrics.fps,
            frameTime: `${this.metrics.frameTime.toFixed(2)}ms`,
            sensorLatency: `${this.metrics.sensorLatency.toFixed(2)}ms`,
            memoryUsage: `${this.metrics.memoryUsage}MB`
        });

        // ì„±ëŠ¥ ê²½ê³  ë°œìƒ
        if (this.metrics.fps < 30) {
            console.warn('âš ï¸ ë‚®ì€ FPS ê°ì§€:', this.metrics.fps);
