        if (this.metrics.sensorLatency > 100) {
            console.warn('âš ï¸ ë†’ì€ ì„¼ì„œ ì§€ì—°ì‹œê°„:', this.metrics.sensorLatency);
        }
    }

    // ì„±ëŠ¥ ë¦¬í¬íŠ¸ ìƒì„±
    generateReport() {
        return {
            timestamp: new Date().toISOString(),
            averageFPS: this.calculateAverage('fps'),
            averageFrameTime: this.calculateAverage('frameTime'),
            averageSensorLatency: this.calculateAverage('sensorLatency'),
            peakMemoryUsage: Math.max(...this.measurements.map(m => m.memoryUsage)),
            recommendations: this.getRecommendations()
        };
    }

    calculateAverage(metric) {
        const values = this.measurements.map(m => m[metric]).filter(v => v > 0);
        return values.reduce((a, b) => a + b, 0) / values.length || 0;
    }

    getRecommendations() {
        const recommendations = [];
        const avgFPS = this.calculateAverage('fps');
        const avgLatency = this.calculateAverage('sensorLatency');

        if (avgFPS < 30) {
            recommendations.push('ë Œë”ë§ ìµœì í™” í•„ìš” - FPSê°€ ë‚®ìŠµë‹ˆë‹¤');
        }

        if (avgLatency > 50) {
