class DOMOptimizer {
    constructor() {
        this.pendingUpdates = new Map();
        this.updateScheduled = false;
    }

    // DOM ì—…ë°ì´íŠ¸ ìŠ¤ì¼€ì¤„ë§
    scheduleUpdate(element, property, value) {
        const elementId = element.id || element.className;

        if (!this.pendingUpdates.has(elementId)) {
            this.pendingUpdates.set(elementId, { element, updates: {} });
        }

        this.pendingUpdates.get(elementId).updates[property] = value;

        if (!this.updateScheduled) {
            this.updateScheduled = true;
            requestAnimationFrame(() => this.flushUpdates());
        }
    }

    // ë°°ì¹˜ ì—…ë°ì´íŠ¸ ì‹¤í–‰
    flushUpdates() {
        this.pendingUpdates.forEach(({ element, updates }) => {
            // CSS ë³€ê²½ ë°°ì¹˜ ì²˜ë¦¬
            Object.keys(updates).forEach(property => {
                if (property.startsWith('style.')) {
                    const styleProp = property.substring(6);
                    element.style[styleProp] = updates[property];
                } else if (property === 'textContent') {
                    element.textContent = updates[property];
                } else if (property === 'innerHTML') {
                    element.innerHTML = updates[property];
                } else {
                    element[property] = updates[property];
                }
            });
        });

        this.pendingUpdates.clear();
        this.updateScheduled = false;
    }

    // DocumentFragmentë¥¼ ì´ìš©í•œ íš¨ìœ¨ì ì¸ DOM ìƒì„±
    createOptimizedList(items, createItemFunction) {
        const fragment = document.createDocumentFragment();

        items.forEach(item => {
            const element = createItemFunction(item);
            fragment.appendChild(element);
        });

        return fragment;
    }

    // ê°€ìƒ ìŠ¤í¬ë¡¤ë§ êµ¬í˜„
    implementVirtualScrolling(container, items, itemHeight, visibleCount) {
        const totalHeight = items.length * itemHeight;
        const viewport = {
            height: visibleCount * itemHeight,
            scrollTop: 0
        };

        // ê°€ìƒ ìŠ¤í¬ë¡¤ ì»¨í…Œì´ë„ˆ ì„¤ì •
        container.style.height = `${viewport.height}px`;
        container.style.overflow = 'auto';

        const content = document.createElement('div');
        content.style.height = `${totalHeight}px`;
        content.style.position = 'relative';

        const visibleItems = document.createElement('div');
        visibleItems.style.position = 'absolute';
        visibleItems.style.top = '0';
        visibleItems.style.width = '100%';

        content.appendChild(visibleItems);
        container.appendChild(content);

        // ìŠ¤í¬ë¡¤ ì´ë²¤íŠ¸ ì²˜ë¦¬
        container.addEventListener('scroll', this.throttle(() => {
            const scrollTop = container.scrollTop;
            const startIndex = Math.floor(scrollTop / itemHeight);
            const endIndex = Math.min(startIndex + visibleCount, items.length);

            // ë³´ì´ëŠ” ì•„ì´í…œë§Œ ë Œë”ë§
            visibleItems.innerHTML = '';
            visibleItems.style.top = `${startIndex * itemHeight}px`;

            for (let i = startIndex; i < endIndex; i++) {
                const itemElement = this.createItemElement(items[i], itemHeight);
                visibleItems.appendChild(itemElement);
            }
        }, 16));
    }
}
