class SensorDataProcessor {
    constructor() {
        this.filters = {
            lowPass: new LowPassFilter(0.8),
            kalman: new KalmanFilter(),
            median: new MedianFilter(5)
        };

        this.calibration = {
            orientation: { x: 0, y: 0, z: 0 },
            acceleration: { x: 0, y: 0, z: 0 }
        };

        this.dataHistory = [];
        this.historySize = 10;
    }

    // ë¡œìš°íŒ¨ìŠ¤ í•„í„° êµ¬í˜„
    applyLowPassFilter(currentValue, previousValue, alpha = 0.8) {
        return {
            x: alpha * previousValue.x + (1 - alpha) * currentValue.x,
            y: alpha * previousValue.y + (1 - alpha) * currentValue.y,
            z: alpha * previousValue.z + (1 - alpha) * currentValue.z
        };
    }

    // ì¹¼ë§Œ í•„í„° êµ¬í˜„ (ë‹¨ìˆœí™”ëœ ë²„ì „)
    applyKalmanFilter(measurement, estimate, processNoise = 0.01, measurementNoise = 0.1) {
        // ì˜ˆì¸¡ ë‹¨ê³„
        const predictedEstimate = estimate;
        const predictedError = processNoise;

        // ì—…ë°ì´íŠ¸ ë‹¨ê³„
        const kalmanGain = predictedError / (predictedError + measurementNoise);
        const newEstimate = predictedEstimate + kalmanGain * (measurement - predictedEstimate);
        const newError = (1 - kalmanGain) * predictedError;

        return { estimate: newEstimate, error: newError };
    }

    // ë©”ë””ì•ˆ í•„í„° êµ¬í˜„
    applyMedianFilter(values) {
        const sorted = [...values].sort((a, b) => a - b);
        const mid = Math.floor(sorted.length / 2);
        return sorted.length % 2 === 0
            ? (sorted[mid - 1] + sorted[mid]) / 2
            : sorted[mid];
    }

    // ì¢…í•© ì„¼ì„œ ë°ì´í„° ì²˜ë¦¬
    processSensorData(rawData) {
        perfMonitor.startFrame();

        // 1. ë°ì´í„° ìœ íš¨ì„± ê²€ì‚¬
        if (!this.validateSensorData(rawData)) {
            return null;
        }

        // 2. ìº˜ë¦¬ë¸Œë ˆì´ì…˜ ì ìš©
        const calibratedData = this.applyCalibraion(rawData);

        // 3. ë…¸ì´ì¦ˆ í•„í„°ë§
        const filteredData = this.applyFilters(calibratedData);

        // 4. ë°ì´í„° ì •ê·œí™”
        const normalizedData = this.normalizeData(filteredData);

        // 5. íˆìŠ¤í† ë¦¬ ì—…ë°ì´íŠ¸
        this.updateHistory(normalizedData);

        perfMonitor.endFrame();
        return normalizedData;
    }

    // ë°ì´í„° ìœ íš¨ì„± ê²€ì‚¬
    validateSensorData(data) {
        if (!data || !data.orientation || !data.acceleration) {
            return false;
        }

        // NaN ê°’ ì²´í¬
        const values = [
            data.orientation.alpha, data.orientation.beta, data.orientation.gamma,
            data.acceleration.x, data.acceleration.y, data.acceleration.z
        ];

        return values.every(value => !isNaN(value) && isFinite(value));
    }

    // ìº˜ë¦¬ë¸Œë ˆì´ì…˜ ì ìš©
    applyCalibraion(data) {
        return {
            orientation: {
                alpha: data.orientation.alpha - this.calibration.orientation.x,
                beta: data.orientation.beta - this.calibration.orientation.y,
                gamma: data.orientation.gamma - this.calibration.orientation.z
            },
            acceleration: {
                x: data.acceleration.x - this.calibration.acceleration.x,
                y: data.acceleration.y - this.calibration.acceleration.y,
                z: data.acceleration.z - this.calibration.acceleration.z
            },
            timestamp: data.timestamp
        };
    }

    // í•„í„° ì ìš©
    applyFilters(data) {
        if (this.dataHistory.length === 0) {
            return data;
        }

        const previousData = this.dataHistory[this.dataHistory.length - 1];

        // ë¡œìš°íŒ¨ìŠ¤ í•„í„° ì ìš©
        const filteredOrientation = this.applyLowPassFilter(
            data.orientation,
            previousData.orientation,
            0.8
        );

        const filteredAcceleration = this.applyLowPassFilter(
            data.acceleration,
            previousData.acceleration,
            0.6
        );

        return {
            orientation: filteredOrientation,
            acceleration: filteredAcceleration,
            timestamp: data.timestamp
        };
    }

    // ë°ì´í„° ì •ê·œí™”
    normalizeData(data) {
        return {
            orientation: {
                alpha: this.clamp(data.orientation.alpha, 0, 360),
                beta: this.clamp(data.orientation.beta, -180, 180),
                gamma: this.clamp(data.orientation.gamma, -90, 90)
            },
            acceleration: {
                x: this.clamp(data.acceleration.x, -20, 20),
                y: this.clamp(data.acceleration.y, -20, 20),
                z: this.clamp(data.acceleration.z, -20, 20)
            },
            timestamp: data.timestamp
        };
    }

    clamp(value, min, max) {
        return Math.max(min, Math.min(max, value));
    }

    // ìë™ ìº˜ë¦¬ë¸Œë ˆì´ì…˜
    autoCalibrate(duration = 3000) {
        return new Promise((resolve) => {
            const samples = [];
            const startTime = Date.now();

            const collectSample = (data) => {
                samples.push(data);

                if (Date.now() - startTime >= duration) {
                    // í‰ê· ê°’ì„ ìº˜ë¦¬ë¸Œë ˆì´ì…˜ ì˜¤í”„ì…‹ìœ¼ë¡œ ì‚¬ìš©
                    this.calibration = this.calculateAverageData(samples);
                    console.log('ğŸ“ Auto-calibration completed:', this.calibration);
                    resolve(this.calibration);
                }
            };

            // ì„ì‹œ ë¦¬ìŠ¤ë„ˆ ë“±ë¡
            document.addEventListener('sensor-data', collectSample);

            setTimeout(() => {
                document.removeEventListener('sensor-data', collectSample);
            }, duration + 100);
        });
    }
}
