class SensorDataSerializer {
    constructor() {
        // ë°ì´í„° ì••ì¶•ì„ ìœ„í•œ ìŠ¤ì¼€ì¼ íŒ©í„°
        this.scales = {
            orientation: 100,    // 0.01ë„ ì •ë°€ë„
            acceleration: 1000,  // 0.001g ì •ë°€ë„
            timestamp: 1         // ms ë‹¨ìœ„
        };

        // ë¹„íŠ¸ ë§ˆìŠ¤í¬ ì •ì˜
        this.ORIENTATION_MASK = 0x001;
        this.ACCELERATION_MASK = 0x002;
        this.ROTATION_RATE_MASK = 0x004;
    }

    // ë°”ì´ë„ˆë¦¬ ì§ë ¬í™” (ìµœëŒ€ ì••ì¶•)
    serializeBinary(sensorData) {
        const buffer = new ArrayBuffer(32);
        const view = new DataView(buffer);
        let offset = 0;

        // íƒ€ì„ìŠ¤íƒ¬í”„ (8ë°”ì´íŠ¸)
        view.setBigUint64(offset, BigInt(sensorData.timestamp), true);
        offset += 8;

        // ì„¼ì„œ ID (1ë°”ì´íŠ¸)
        view.setUint8(offset, this.getSensorIdCode(sensorData.sensorId));
        offset += 1;

        // ë°ì´í„° ë§ˆìŠ¤í¬ (1ë°”ì´íŠ¸)
        let mask = 0;
        if (sensorData.orientation) mask |= this.ORIENTATION_MASK;
        if (sensorData.acceleration) mask |= this.ACCELERATION_MASK;
        if (sensorData.rotationRate) mask |= this.ROTATION_RATE_MASK;
        view.setUint8(offset, mask);
        offset += 1;

        // Orientation ë°ì´í„° (12ë°”ì´íŠ¸)
        if (sensorData.orientation) {
            view.setInt16(offset, sensorData.orientation.alpha * this.scales.orientation, true);
            view.setInt16(offset + 2, sensorData.orientation.beta * this.scales.orientation, true);
            view.setInt16(offset + 4, sensorData.orientation.gamma * this.scales.orientation, true);
            offset += 6;
        }

        // Acceleration ë°ì´í„° (12ë°”ì´íŠ¸)
        if (sensorData.acceleration) {
            view.setInt16(offset, sensorData.acceleration.x * this.scales.acceleration, true);
            view.setInt16(offset + 2, sensorData.acceleration.y * this.scales.acceleration, true);
            view.setInt16(offset + 4, sensorData.acceleration.z * this.scales.acceleration, true);
            offset += 6;
        }

        return buffer.slice(0, offset);
    }

    // ë°”ì´ë„ˆë¦¬ ì—­ì§ë ¬í™”
    deserializeBinary(buffer) {
        const view = new DataView(buffer);
        let offset = 0;

        // íƒ€ì„ìŠ¤íƒ¬í”„
        const timestamp = Number(view.getBigUint64(offset, true));
        offset += 8;

        // ì„¼ì„œ ID
        const sensorIdCode = view.getUint8(offset);
        const sensorId = this.getSensorIdFromCode(sensorIdCode);
        offset += 1;

        // ë°ì´í„° ë§ˆìŠ¤í¬
        const mask = view.getUint8(offset);
        offset += 1;

        const result = { sensorId, timestamp, data: {} };

        // Orientation ë°ì´í„°
        if (mask & this.ORIENTATION_MASK) {
            result.data.orientation = {
                alpha: view.getInt16(offset, true) / this.scales.orientation,
                beta: view.getInt16(offset + 2, true) / this.scales.orientation,
                gamma: view.getInt16(offset + 4, true) / this.scales.orientation
            };
            offset += 6;
        }

        // Acceleration ë°ì´í„°
        if (mask & this.ACCELERATION_MASK) {
            result.data.acceleration = {
                x: view.getInt16(offset, true) / this.scales.acceleration,
                y: view.getInt16(offset + 2, true) / this.scales.acceleration,
                z: view.getInt16(offset + 4, true) / this.scales.acceleration
            };
            offset += 6;
        }

        return result;
    }

    // ì••ì¶•ëœ JSON ì§ë ¬í™”
    serializeCompressed(sensorData) {
        const compressed = {
            t: sensorData.timestamp,
            s: this.getSensorIdCode(sensorData.sensorId)
        };

        // ë°ì´í„° ì••ì¶•
        if (sensorData.data.orientation) {
            compressed.o = [
                Math.round(sensorData.data.orientation.alpha * 100),
                Math.round(sensorData.data.orientation.beta * 100),
                Math.round(sensorData.data.orientation.gamma * 100)
            ];
        }

        if (sensorData.data.acceleration) {
            compressed.a = [
                Math.round(sensorData.data.acceleration.x * 1000),
                Math.round(sensorData.data.acceleration.y * 1000),
                Math.round(sensorData.data.acceleration.z * 1000)
            ];
        }

        return JSON.stringify(compressed);
    }

    // ì••ì¶•ëœ JSON ì—­ì§ë ¬í™”
    deserializeCompressed(jsonString) {
        const compressed = JSON.parse(jsonString);

        const result = {
            timestamp: compressed.t,
            sensorId: this.getSensorIdFromCode(compressed.s),
            data: {}
        };

        if (compressed.o) {
            result.data.orientation = {
                alpha: compressed.o[0] / 100,
                beta: compressed.o[1] / 100,
                gamma: compressed.o[2] / 100
            };
        }

        if (compressed.a) {
            result.data.acceleration = {
                x: compressed.a[0] / 1000,
                y: compressed.a[1] / 1000,
                z: compressed.a[2] / 1000
            };
        }

        return result;
    }

    // ì„¼ì„œ ID ì½”ë“œ ë³€í™˜
    getSensorIdCode(sensorId) {
        const codes = { 'sensor1': 1, 'sensor2': 2, 'sensor3': 3, 'sensor': 0 };
        return codes[sensorId] || 0;
    }

    getSensorIdFromCode(code) {
        const ids = { 1: 'sensor1', 2: 'sensor2', 3: 'sensor3', 0: 'sensor' };
        return ids[code] || 'sensor';
    }

    // ì••ì¶•ë¥  í…ŒìŠ¤íŠ¸
    testCompressionRatio(sensorData) {
        const original = JSON.stringify(sensorData);
        const compressed = this.serializeCompressed(sensorData);
        const binary = this.serializeBinary(sensorData);

        console.log('ğŸ“Š Compression Test:');
        console.log(`Original: ${original.length} bytes`);
        console.log(`Compressed JSON: ${compressed.length} bytes (${((1 - compressed.length / original.length) * 100).toFixed(1)}% savings)`);
        console.log(`Binary: ${binary.byteLength} bytes (${((1 - binary.byteLength / original.length) * 100).toFixed(1)}% savings)`);

        return {
            original: original.length,
            compressed: compressed.length,
            binary: binary.byteLength
        };
    }
}
