        // ì˜ˆì¸¡ ë™ê¸°í™” ê°•í™”
        this.enhancePredictiveSync(scalingFactor);
        improvement += 20;

        return improvement * scalingFactor;
    }

    reduceNetworkLoad(resourceAllocation) {
        let improvement = 0;

        switch (resourceAllocation) {
            case 'distributed':
                // P2P í†µì‹  í™œì„±í™”
                this.enablePeerToPeerCommunication();
                improvement += 35;
                break;

            case 'grouped':
                // ê·¸ë£¹ ê¸°ë°˜ í†µì‹ 
                this.enableGroupedCommunication();
