        for (const collision of collisions) {
            // AI ê¸°ë°˜ ì¶©ëŒ ì‘ë‹µ ê³„ì‚°
            const response = this.responseEngine.calculateResponse(collision, gameState);

            // ë¬¼ë¦¬ ì‘ë‹µ ì ìš©
