            // ê²Œì„ ë¡œì§ ì‘ë‹µ ì ìš©
            this.applyGameLogicResponse(collision, response, gameState);

            // ì‹œê°/ìŒí–¥ íš¨ê³¼ ì²˜ë¦¬
            this.triggerCollisionEffects(collision, response);

            resolvedCollisions.push({
                collision,
                response,
                timestamp: Date.now()
