        return {
            predicted: !!prediction,
            confidence: prediction ? prediction.confidence : 0,
            severity: this.calculateCollisionSeverity(pair),
            type: this.classifyCollisionType(pair),
            timing: prediction ? prediction.estimatedTime : null
        };
    }
}
