    updateGameState(input) {
        const deltaTime = 16; // 60fps ê¸°ì¤€

        // ë¬¼ë¦¬ ì—”ì§„ ì—…ë°ì´íŠ¸
        const physicsResult = this.physicsEngine.update(deltaTime, this.gameState.gameState, input);

