        // ë‹¤ìŒ í”„ë ˆì„ ì˜ˆì•½
        requestAnimationFrame(() => this.gameLoop());
    }

    render() {
        // í™”ë©´ ì§€ìš°ê¸°
        this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

        // ì ì‘í˜• ë Œë”ë§ ì ìš©
        const renderQuality = this.performanceOptimizer.getCurrentRenderQuality();

        // ë°°ê²½ ë Œë”ë§
        this.renderBackground(renderQuality);

        // í”Œë ˆì´ì–´ ë Œë”ë§
