        if (this.previousData) {
            return {
                alpha: Math.abs(data.alpha - this.previousData.alpha),
                beta: Math.abs(data.beta - this.previousData.beta),
                gamma: Math.abs(data.gamma - this.previousData.gamma)
            };
        }

