        this.filterCoefficients.gamma = Math.max(0.1,
            Math.min(0.99, this.filterCoefficients.gamma - error.gamma * this.adaptationRate));
    }

    applyAdaptiveFilter(data) {
        if (!this.previousFiltered) {
            this.previousFiltered = data;
