    applyAdaptiveOptimization(result, prediction) {
        const optimized = { ...result };

        // ì˜ˆì¸¡ëœ ì„±ëŠ¥ì´ ë‚®ìœ¼ë©´ ë³´ì • ì ìš©
        if (prediction.confidence < 0.7) {
