class AdaptiveFusionSelector {
    constructor() {
        this.enginePerformance = {
            complementary: { success_rate: 0.8, avg_latency: 2, quality_score: 0.7 },
            kalman: { success_rate: 0.9, avg_latency: 5, quality_score: 0.85 },
