    calculateOffsets() {
        if (this.calibrationSamples.length === 0) {
            throw new Error('ìº˜ë¦¬ë¸Œë ˆì´ì…˜ ìƒ˜í”Œì´ ì—†ìŠµë‹ˆë‹¤.');
