    async verifyCalibration() {
        // ìº˜ë¦¬ë¸Œë ˆì´ì…˜ ê²€ì¦ì„ ìœ„í•œ ì¶”ê°€ ìƒ˜í”Œ ìˆ˜ì§‘
        const verificationSamples = [];
        const requiredVerificationSamples = 20;

        return new Promise((resolve) => {
            let sampleCount = 0;

